SELECT MEMBER_NAME, REVIEW_TEXT, DATE_FORMAT(REVIEW_DATE, '%Y-%m-%d') as REVIEW_DATE from MEMBER_PROFILE as m JOIN REST_REVIEW as r USING (MEMBER_ID) where m.MEMBER_ID IN (SELECT * from (SELECT MEMBER_ID from REST_REVIEW group by MEMBER_ID order by count(*) desc limit 1) as t) order by REVIEW_DATE, REVIEW_TEXT